          ptr = (char *)((uint32_t)chromatix_ptr + item.offset +
          table_index * sizeof(double));
          break;
        case EZT_D_EXPENT:
        case EZT_D_INVALID:
          CDBG_EZ("%s :: Invalid Data Item \n",__func__);
