/*===========================================================================
 * FUNCTION     - eztune_set_item_data -
 *
 * DESCRIPTION: Sets each member of the item structure.
 * =========================================================================*/
void eztune_set_item_data (eztune_item_t *item_ptr,
                          char *name,
                          eztune_item_data_t data,
                          eztune_item_type_t type,
                          eztune_item_reg_flag_t reg_flag,
                          uint32_t offset,
                          uint16_t size)
{
  item_ptr->name = name;
  item_ptr->data = data;
  item_ptr->type = type;
  item_ptr->reg_flag = reg_flag;
  item_ptr->offset = offset;
  item_ptr->size = size;
}
